import React, { useState, useEffect } from 'react';
import { HabitTracker } from './components/HabitTracker';
import { Login } from './components/Login';
import { auth } from './config/firebase';
import { onAuthStateChanged, User, signOut } from 'firebase/auth';
import './App.css';

function App() {
  const [user, setUser] = useState<User | null>(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Listen for auth state changes
    const unsubscribe = onAuthStateChanged(auth, (currentUser) => {
      setUser(currentUser);
      setLoading(false);
    });

    return () => unsubscribe();
  }, []);

  const handleSignOut = async () => {
    try {
      await signOut(auth);
    } catch (error) {
      console.error('Error signing out:', error);
    }
  };

  if (loading) {
    return (
      <div className="loading-container">
        <div className="loading-spinner">Loading...</div>
      </div>
    );
  }

  if (!user) {
    return (
      <div className="error-container">
        <h2>Setup Required</h2>
        <p>To use Firebase, please:</p>
        <ol style={{ textAlign: 'left', maxWidth: '500px', margin: '20px auto' }}>
          <li>Go to <a href="https://console.firebase.google.com" target="_blank" rel="noopener noreferrer" style={{ color: '#60a5fa' }}>Firebase Console</a></li>
          <li>Select your "humanetracker" project</li>
          <li>Enable <strong>Anonymous Authentication</strong> in Authentication â†’ Sign-in method</li>
          <li>Create <strong>Firestore Database</strong> in test mode</li>
          <li>Refresh this page</li>
        </ol>
        <p style={{ marginTop: '30px' }}>
          <button 
            onClick={() => window.location.reload()} 
            style={{ 
              padding: '10px 20px', 
              background: '#60a5fa', 
              color: '#1a1a1a', 
              border: 'none', 
              borderRadius: '6px',
              cursor: 'pointer',
              fontSize: '14px',
              fontWeight: '500'
            }}
          >
            Retry Connection
          </button>
        </p>
      </div>
    );
  }

  return (
    <div className="App">
      {useMockMode && (
        <div style={{ 
          background: '#fbbf24', 
          color: '#1a1a1a', 
          padding: '8px', 
          textAlign: 'center',
          fontSize: '12px'
        }}>
          Mock Mode - Firebase not configured
        </div>
      )}
      <HabitTracker userId={user.uid} />
    </div>
  );
}

export default App;
