import React, { useState, useEffect } from 'react';
import { HabitTracker } from './components/HabitTracker';
import { auth } from './config/firebase';
import { signInAnonymously, onAuthStateChanged, User } from 'firebase/auth';
import './App.css';

function App() {
  const [user, setUser] = useState<User | null>(null);
  const [loading, setLoading] = useState(true);
  const [useMockMode, setUseMockMode] = useState(false);

  useEffect(() => {
    // Check if we're in test/dev mode without proper Firebase setup
    const isTestMode = process.env.NODE_ENV === 'development' || window.location.hostname === 'localhost';

    // Listen for auth state changes
    const unsubscribe = onAuthStateChanged(auth, async (currentUser) => {
      if (!currentUser) {
        // Sign in anonymously if no user
        try {
          await signInAnonymously(auth);
        } catch (error) {
          console.error('Error signing in anonymously:', error);
          // Log full error details
          console.error('Full error details:', JSON.stringify(error, null, 2));

          // If auth fails and we're in test mode, log the error
          if (isTestMode) {
            console.error('Firebase authentication failed. Please check your configuration.');
            // Don't auto-fallback to mock mode - let user see the error
          }
        }
      } else {
        setUser(currentUser);
      }
      setLoading(false);
    }, (error) => {
      console.error('Auth state change error:', error);
      // Use mock mode if Firebase is not configured
      if (isTestMode) {
        console.log('Firebase not configured, using mock mode');
        setUseMockMode(true);
        setUser({ uid: 'test-user-123' } as User);
        setLoading(false);
      }
    });

    return () => unsubscribe();
  }, []);

  if (loading) {
    return (
      <div className="loading-container">
        <div className="loading-spinner">Loading...</div>
      </div>
    );
  }

  if (!user) {
    return (
      <div className="error-container">
        <p>Unable to authenticate. Please refresh the page.</p>
      </div>
    );
  }

  return (
    <div className="App">
      {useMockMode && (
        <div style={{ 
          background: '#fbbf24', 
          color: '#1a1a1a', 
          padding: '8px', 
          textAlign: 'center',
          fontSize: '12px'
        }}>
          Mock Mode - Firebase not configured
        </div>
      )}
      <HabitTracker userId={user.uid} />
    </div>
  );
}

export default App;
